/*--------Color Palette-----------*/
:root {
--yellow: rgba(249, 200, 14, 1);
--white: rgba(247, 243, 227, 1);
/* --orange: rgba(231, 147, 33, 1); */
--orange: #dfb8a9;
--blue: rgba(98, 188, 204, 1);
--black: rgba(14, 15, 25, 1);

--offset: 10px;
--border-size: 2px;
}

body {
  font-family: 'Source Sans Pro', sans-serif;
  margin: 0;
  padding: 0;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

a {
  text-decoration: none;
}
/*-----Main Landing page----*/
.main-landing {
  background-color: var(--black);
  color: var(--white);
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;
}
.main-landing-name {
  border: 1px var(--white) solid;
  font-size: 9em;
  font-weight: 700;
  height: 50vh;
  text-align: center;
  transform: rotate(30deg);
  width: 50vh;

  display: flex;
  justify-content: center;
  align-items: center;

  animation-name: spin;
  animation-duration: 50000ms;
  animation-iteration-count: infinite;
  animation-timing-function: linear;
}

@media screen and (max-width: 992px){
  .main-landing-name{
    font-size: 8em;
    font-weight: 600;
    height: 40vh;
    text-align: center;
    transform: rotate(30deg);
    width: 40vh;
  }
}
@media screen and (max-width: 768px){
  .main-landing-name{
    font-size: 475%;
    font-weight: 600;
    height: 28vh;
    text-align: center;
    transform: rotate(30deg);
    width: 28vh;
    overflow: visible;
  }
}
@keyframes spin {
    from {
        transform:rotate(0deg);
    }
    to {
        transform:rotate(360deg);
    }
}

/*--------------------Navigation Items--------------------*/

.navigation-list {
  color: var(--white);
  list-style: none;
  position: fixed;
  height: 100%;
  transform: rotate(90deg);
  width: 15vw;
  /*z-index: 100;*/

  display: flex;
  align-items: center;
  justify-content: center;
}

.navigation-list-item {
  font-size: 25px;
  margin: 0 20px;
}

.navigation-list-item:hover {
  cursor: pointer;
  transform: rotate(-90deg);
  transition: 400ms;
}

@media screen and (max-width: 992px){
  .navigation-list-item:hover{
    transform: none;
  }
}
@media screen and (max-width: 768px){
  .navigation-list-item:hover{
    transform: none;
  }
}


/*--------------------About Page--------------------*/
.about-page {
  background-color: var(--black);
  height: 100vh;
  padding: 0;
  margin: 0;

  display: flex;
  align-items: center;
  justify-content: center;
}

.about-flexbox{
  display: flex;
  align-items: center;
  justify-content: center;
}

.about-flexbox-left{
  background-color: var(--orange);
  height: 40%;
  width: 30%;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
}

.photo {
  height: 80vh;
  width: 60vh;
  border-radius: 30px;
}
.about-description-hello{
  color: var(--white);
  font-size: 7em;
  letter-spacing: 10px;
  transform: rotate(15deg);
  margin: 0;
  padding: 0;
}



.about-description-content {
  font-weight: 300;
  margin: 1em 2em;
}

@media screen and (max-width: 1220px){
  .about-page{
    height: auto;
  }

.about-description-hello{
  font-size: 6em;
  letter-spacing: 10px;
}
.photo {
  height: 40%;
  width: 30%;
  border-radius: 30px;
}

.about-flexbox-left{
  height: auto;
  width: 30%;
}
}

@media screen and (max-width: 992px){
  .about-page{
    height: auto;
    padding-top: 15px;
  }

.about-description-hello{
  font-size: 6em;
  letter-spacing: 10px;
}
.photo {
  height: 55vh;
  width: 40vh;
  border-radius: 30px;
}

.about-flexbox-left{
  height: auto;
  width: 40vh;
}

}
@media screen and (max-width: 768px){
  body {
    max-width: 100%;
  }

  .about-page{
    height: auto;
  }

  .about-flexbox{
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
}

.about-description-hello{
  font-size: 5em;
  letter-spacing: 10px;
}
.photo {
  height: auto;
  width: 65%;
  border-radius: 30px;
  margin-bottom: 3%;
}

.about-flexbox-left{
  height: auto;
  width: 65%;
   border-radius: 30px;
}

}


