.work-photo{
  filter: brightness(80%);
}

.divImg {
  position: relative;
  overflow: hidden;
}
.divImg .caption {
    height: 100%;
    width: 100%;
		cursor: pointer;
		position: absolute;
    top: 0;
    transform: scaleY(0);
    transform-origin: top;
    transition: transform 0.35s ease;
}
.effect1:hover .caption {
  transform: scaleY(1);
}
.caption-text h1{
		text-transform: uppercase;
		font-size: 2em;
}
.caption-text{
		z-index: 5;
		color: var(--white);
    position: absolute;
    width: 100%;
    top: 50%;
    transform: translateY(-50%);
    text-align: center;
}
.blur {
		background-color: rgba(0,0,0,0.5);
		height: 100%;
		width: 100%;
    position: absolute;
    top: 0;
}

